import React, { useState } from 'react';
import './Styles.css';
export default function App(prop) {
  const [task, setTask] = useState('');
  const [listTask, setListTask] = useState([]);

  const onSubmit = () => {
    const special = /[!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?]/;
    console.log(task.match(special));
    if (task !== '' && task.match(special)=== null) {
      setListTask([...listTask, task]);
      setTask('');
    }
    else {
      alert('Task invalid');
    }
  }
  const onDelete = (item) => {
    console.log('item', item);
    const newArray = listTask.filter(e => e !==  item);
    setListTask(newArray);
  }

  console.log('=====' + listTask);
  return (
    <div className='header'>
      <h2 >To Do</h2>
      <div>
        <span>Add Task : </span>
        <input
          type='text'
          className='textinput'
          placeholder='What needs to be done?'
          onChange={(event) => { setTask(event.target.value) }}
          value={task} />
        <button type='button' value='Submit' className='buttonsubmit' onClick={onSubmit}>Add</button>
      </div>
      {listTask.map((item, index) => {
        return <div className = {index %2 === 0 ? 'green':'yellow' } >
          <input type='checkbox' />
          <label> {item}</label>
          <button className='btColor' onClick={()=>onDelete(item)}>Delete</button>
        </div>
      })}
      <div className='footer'>
        <ul >
          <li >{listTask.length} item left</li>
          <li><button onClick>All</button></li>
          <li><button >Active</button></li>
          <li><button >Completed</button></li>
          <li><button >Clear all task completed</button></li>
        </ul>
      </div>
    </div>
  );
}
