import React, { useState } from 'react';
import InputForm from './components/InputForm';
import Todos from './components/Todos';
import Filter from './components/Filter';
function App() {

  const [items, setItems] = useState([]);
  const array = [...items];
  const [input, setInput] = useState('');

  const onSubmit = (task) => {
    var number = Math.random() // 0.9394456857981651
    number.toString(36); // '0.xtis06h6'
    var id = number.toString(36).substr(2, 9); // 'xtis06h6'
    const todoTask = {
      id: id,
      status:null,
      content: task,
    }
    setItems([...items, todoTask]);
    setInput('');
  }

  const onSubmitDelete = (id) => {
    const data = items.filter(e => e.id !==  id)
    setItems(data);
  }

  return (
    <div>
      <InputForm onSubmit={onSubmit} input={input} setInput={setInput}/>
      <Todos items={items} onSubmitDelete={onSubmitDelete}  />
      <Filter items={items} setItems={setItems} array={array} setArray={setArray}/>
    </div>
  );
}

export default App;
