import React, { useState } from 'react';
import './Styles.css';
export default function App(prop) {
  const [task, setTask] = useState('');
  const [listTask, setListTask] = useState(['hello','big']);
  function hanldeOnSubmit() {
    setListTask(listTask.push(task));
    setTask('');
    console.log(listTask);
    alert(listTask);
  }
  return (
    <div className='header'>
      <h2>To Do</h2>
      <h3>{task}</h3>
      <div>
        <form onSubmit={hanldeOnSubmit}>
          Add Task :
           <input type='text' name='task' onChange={event => setTask(event.target.value)} />
           <input type='submit' value='Submit' />
        </form>
      </div>
    </div>
  );
}

function Con(props) {
  return (
    <div>
      <p>{props.name}</p>
    </div>
  );
}
