import React, { useState } from 'react';
import './Style.css';
import Footer from './Footer';
const TodoItem = (props) => {
  const [check, setCheck] = useState(false);
  const onCheck = () => {
    setCheck(prev => !prev);
  }
  return (
    <div>
      <div className='listItems'>
        <input type='checkbox' id='myCheck' onClick={()=>onCheck()}/>
        <label id='content'>
          {check ? (<span><strike>{props.item.content}</strike></span>):props.item.content}
        </label>
        <button className='DeleteHide' type='button' onClick={() => props.onSubmitDelete(props.item.id)}>Delete</button>
      </div>
    </div>
  );
}
export default TodoItem;
