import React,{useState} from 'react';
const TodoItem = (props) => {
  const [check, setCheck] = useState(false);
  const onCheck = () => {
    setCheck(prev => !prev);
  }
  return (
    <div>
      <input type='checkbox' id='myCheck' onClick={()=>onCheck()}/>
      <label id='content'>
        {check ? (<strike>{props.item.content}</strike>):props.item.content}
      </label>
      <button  type='button' onClick={()=>props.onSubmitDelete(props.item.id)}>Delete</button>
    </div>
  );
}
export default TodoItem;
