import React,{useState} from 'react';
export default function App() {
  const [task, setTask] = useState('');
  const [listTask, setListTask] = useState(['hello','big']);
  function hanldeOnSubmit() {
    setListTask(listTask.push(task));
    setTask('');
    console.log(listTask);
    alert(listTask);
  }
  console.log
  return (
    <div style={{ textAlign: 'center' }}>
      <h2>WEB APP TO DO</h2>
      <h3>{task}</h3>
      <div>
        <form onSubmit={hanldeOnSubmit}>
          Add Task :
           <input type='text' name='task' onChange={event => setTask(event.target.value)} />
           <input type='submit' value='Submit' />
        </form>
        <div>
          <ul>
            <Con name='Thuong'/>
          </ul>
        </div>
      </div>
    </div>
  );
}

function Con(prop) {
  return (
    <div>
      <p>{prop.name}</p>
    </div>
  );
}
