import React, { useState } from 'react';
import './Styles.css';
export default function App(prop) {
  const [task, setTask] = useState('');
  const [listTask, setListTask] = useState([]);

   const onSubmit = () => {
    setListTask(listTask.concat(...listTask,task));

   }

  console.log('listtask=====' + listTask);

  return (
    <div className='header'>
      <h2 >To Do</h2>
      <div>
        <span>Add Task : </span>
        <input
          type='text'
          className='textinput'
          placeholder='What needs to be done?'
          onChange={(event) => { setTask(event.target.value) }}
          value={task} />
        <button type='button' value='Submit' className='buttonsubmit' onClick={onSubmit}>submit</button>
      </div>
      <div className='listTask'>
        <input type='checkbox'/>
        {/* <label>{listTask}</label> */}
        {listTask.map(item => {return <label>{listTask}</label>})}
      </div>
      <div className='footer'>
        <ul >
          <li ><a href='0'>item left</a></li>
          <li><a href='1'>All</a></li>
          <li><a href='2'>Active</a></li>
          <li><a href='3'>Completed</a></li>
          <li><a href='4'>Clear all task completed</a></li>
        </ul>
      </div>
    </div>
  );
}
