import React, { useState } from 'react';
import './Styles.css';
export default function App(prop) {
  const [task, setTask] = useState('');
  const [listTask, setListTask] = useState([]);

  const onSubmit = () => {
    const special = /[!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?]/;
    console.log(task.match(special));
    if (task !== '' && task.match(special)=== null) {
      setListTask([...listTask, task]);
      setTask('');
    }
    else {
      alert('Task invalid');
    }
  }
  const onDelete = (index) => {
    console.log('index', index);
    newArray = [...listTask.splice(index, 1)];
  }

  console.log('=====' + listTask);
  return (
    <div className='header'>
      <h2 >To Do</h2>
      <div>
        <span>Add Task : </span>
        <input
          type='text'
          className='textinput'
          placeholder='What needs to be done?'
          onChange={(event) => { setTask(event.target.value) }}
          value={task} />
        <button type='button' value='Submit' className='buttonsubmit' onClick={onSubmit}>Add</button>
      </div>
      {listTask.map((item, index) => {
        return <div className = {index %2 === 0 ? 'green':'yellow' } >
          <input type='checkbox' />
          <label>{item}</label>
          <button className='btColor' onClick={()=>onDelete(index)}>Delete</button>
        </div>
      })}
      <div className='footer'>
        <ul >
          <li ><a href='0'>item left</a></li>
          <li><a href='1'>All</a></li>
          <li><a href='2'>Active</a></li>
          <li><a href='3'>Completed</a></li>
          <li><a href='4'>Clear all task completed</a></li>
        </ul>
      </div>
    </div>
  );
}
