import React,{useState} from 'react';
export default function App() {
  const [task, setTask] = useState('');
  const [listTask, setListTask] = useState(['hello','big']);
  function hanldeOnSubmit() {
    const arraylist = [...listTask];
    arraylist.push(task);
    setListTask(arraylist);
    setTask('');
  }


  return (
    <div style={{ textAlign: 'center' }}>
      <h2>WEB APP TO DO</h2>
      <h3>{task}</h3>
      <div>
        <form onSubmit={hanldeOnSubmit}>
          Add Task :
           <input type='text' name='task' onChange={event => setTask(event.target.value)} />
           <input type='submit' value='Submit' />
        </form>
      </div>
    </div>
  );
}
